mapper.select:
select * ,sf_get_nickname(regi_id) as nickName from board where board_id = #{board_id}
and subject like CONCAT('%',#{search},'%')
and sf_get_nickname(regi_id) like CONCAT('%',#{search},'%')
and content like CONCAT('%',#{search},'%')
order by seq desc limit #{startRow}, #{pageSize}

select
*,
sf_get_filePath(seq) as path1
from
board where board_id=#{board_id}
order by seq desc;

select
*,
sf_get_filePath(seq) as path1
from
board where board_id=#{board_id}
order by recommend desc;

select count(*) from board where board_id=#{board_id}
and subject like CONCAT('%',#{search},'%')
and regi_id like CONCAT('%',#{search},'%')

select count(*) from board where regi_id = #{regi_id}
board_id = #{board_id}
and subject like CONCAT('%',#{search},'%')
and sf_get_nickname(regi_id) like CONCAT('%',#{search},'%')
and content like CONCAT('%',#{search},'%')
and not board_id in ('gallery')

select *,sf_get_nickname(regi_id) as nickName from board where board_id=#{board_id} and etc1=#{etc1}
and subject like CONCAT('%',#{search},'%')
and sf_get_nickname(regi_id) like CONCAT('%',#{search},'%')
and content like CONCAT('%',#{search},'%')
order by seq desc limit #{startRow}, #{pageSize}

select
*,
sf_get_filePath(seq) as path1
from
board where board_id=#{board_id} and regi_id = #{regi_id}
order by seq desc;

select * ,sf_get_nickname(regi_id) as nickName from board where regi_id = #{regi_id}
board_id = #{board_id}
and subject like CONCAT('%',#{search},'%')
and sf_get_nickname(regi_id) like CONCAT('%',#{search},'%')
and content like CONCAT('%',#{search},'%')
and not board_id in ('gallery') order by seq desc limit #{startRow}, #{pageSize}

update 
	board_comment 
set 
	likeCount = #{like} 
where 
	seq = #{seq}

update 
	board_comment 
set 
	hateCount = #{hate} 
where 
	seq = #{seq}
